import React, { useState, useEffect } from 'react';
import { Button, Card, Select, message } from 'antd';
import ReactQuill from 'react-quill';
import axios from 'axios';
import 'react-quill/dist/quill.snow.css';
import LanguageSelector from '../LanguageSelector/LanguageSelector';
import './FAQEditor.css';

const FAQEditor = () => {
  const [faqs, setFaqs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedLang, setSelectedLang] = useState('en');
  const [formData, setFormData] = useState({
    question: '',
    answer: '',
    language: 'en'
  });

  const LANGUAGES = [
    { code: 'en', name: 'English' },
    { code: 'hi', name: 'Hindi' },
    { code: 'bn', name: 'Bengali' }
    // Add other languages as needed
  ];

  useEffect(() => {
    const fetchFAQs = async () => {
      try {
        setLoading(true);
        const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/faqs?lang=${selectedLang}`);
        // Assuming the API returns an object with a "data" property
        setFaqs(data.data || []);
      } catch (error) {
        message.error('Error loading FAQs');
      } finally {
        setLoading(false);
      }
    };
    fetchFAQs();
  }, [selectedLang]);

  const handleSubmit = async () => {
    try {
      setLoading(true);
      const { data } = await axios.post(`${process.env.REACT_APP_API_URL}/faqs`, {
        ...formData,
        autoTranslate: true
      });
      setFaqs([data, ...faqs]);
      message.success('FAQ created successfully!');
      setFormData({ question: '', answer: '', language: 'en' });
    } catch (error) {
      message.error('Error creating FAQ');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      setLoading(true);
      await axios.delete(`${process.env.REACT_APP_API_URL}/faqs/${id}`);
      setFaqs(faqs.filter(faq => faq._id !== id));
      message.success('FAQ deleted successfully!');
    } catch (error) {
      message.error('Error deleting FAQ');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <Card 
        title="FAQ Manager" 
        extra={<LanguageSelector value={selectedLang} onChange={setSelectedLang} style={{ width: 120 }} />}
      >
        <Select
          options={LANGUAGES.map(lang => ({ value: lang.code, label: lang.name }))}
          value={formData.language}
          onChange={lang => setFormData(prev => ({ ...prev, language: lang }))}
          style={{ width: 200, marginBottom: 16 }}
        />

        <input
          placeholder="Enter question"
          value={formData.question}
          onChange={e => setFormData(prev => ({ ...prev, question: e.target.value }))}
          className="input-field"
        />

        <ReactQuill
          theme="snow"
          value={formData.answer}
          onChange={value => setFormData(prev => ({ ...prev, answer: value }))}
          modules={{
            toolbar: [
              ['bold', 'italic', 'underline'],
              ['link', 'blockquote'],
              [{ list: 'ordered' }, { list: 'bullet' }]
            ]
          }}
        />       
        <Button
          type="primary"
          onClick={handleSubmit}
          loading={loading}
          disabled={!formData.question || !formData.answer}
          style={{ marginTop: 16 }}
        >
          Save FAQ
        </Button>

        <div className="faq-list">
          {faqs.map(faq => (
            <Card key={faq._id} style={{ marginTop: 16 }}>
              <div className="card-header">
                <h3>{faq.question}</h3>
                <div className="language-tag">{faq.language.toUpperCase()}</div>
              </div>
              <div className="answer-content" dangerouslySetInnerHTML={{ __html: faq.answer }} />
              <Button 
                type="link" 
                danger 
                onClick={() => handleDelete(faq._id)}
                style={{ marginTop: 8 }}
              >
                Delete
              </Button>
            </Card>
          ))}
        </div>
      </Card>
    </div>
  );
};

export default FAQEditor;
