import React, { useState, useEffect } from 'react';
import { Button, Card, Select, message, Input } from 'antd';
import ReactQuill from 'react-quill';
import axios from 'axios';
import 'react-quill/dist/quill.snow.css';
import LanguageSelector from '../LanguageSelector/LanguageSelector';
import './FAQEditor.css';

const FAQEditor = () => {
  const [faqs, setFaqs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedLang, setSelectedLang] = useState('en');
  const [formData, setFormData] = useState({
    question: '',
    answer: '',
    language: 'en'
  });

  const LANGUAGES = [
    { code: 'en', name: 'English' },
    { code: 'hi', name: 'Hindi' },
    { code: 'bn', name: 'Bengali' }
    // Add other languages as needed
  ];

  useEffect(() => {
    fetchFAQs();
  }, [selectedLang]);

  const fetchFAQs = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:5000/api/faqs?language=${selectedLang}`);
      setFaqs(response.data);
    } catch (err) {
      message.error('Error fetching FAQs');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.question || !formData.answer) {
      message.error('Both question and answer are required.');
      return;
    }

    try {
      setLoading(true);
      const response = await axios.post('http://localhost:5000/api/faqs', formData);
      message.success('FAQ created successfully!');
      setFaqs(prevFaqs => [response.data, ...prevFaqs]);
      setFormData({
        question: '',
        answer: '',
        language: 'en'
      });
    } catch (error) {
      if (error.response) {
        message.error(`Error: ${error.response.data.message}`);
        console.error('Server error:', error.response.data);
      } else {
        message.error('Error creating FAQ');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      setLoading(true);
      await axios.delete(`http://localhost:5000/api/faqs/${id}`);
      setFaqs(faqs.filter(faq => faq._id !== id));
      message.success('FAQ deleted successfully!');
    } catch (error) {
      message.error('Error deleting FAQ');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <Card 
        title="FAQ Manager" 
        extra={<LanguageSelector value={selectedLang} onChange={setSelectedLang} style={{ width: 120 }} />}
      >
        <form onSubmit={handleSubmit}>
          <Input
            placeholder="Enter question"
            value={formData.question}
            onChange={(e) => setFormData({ ...formData, question: e.target.value })}
            className="input-field"
            style={{ marginBottom: 16 }}
          />
          <Input.TextArea
            placeholder="Enter answer"
            value={formData.answer}
            onChange={(e) => setFormData({ ...formData, answer: e.target.value })}
            className="input-field"
            rows={4}
            style={{ marginBottom: 16 }}
          />
          <Button type="primary" htmlType="submit">Submit</Button>
        </form>

        <div className="faq-list" style={{ marginTop: 24 }}>
          {faqs.map(faq => (
            <Card key={faq._id} style={{ marginTop: 16 }}>
              <div className="card-header">
                <h3>{faq.question}</h3>
                <div className="language-tag">{faq.language.toUpperCase()}</div>
              </div>
              <div className="answer-content">{faq.answer}</div>
              <Button 
                type="link" 
                danger 
                onClick={() => handleDelete(faq._id)}
                style={{ marginTop: 8 }}
              >
                Delete
              </Button>
            </Card>
          ))}
        </div>
      </Card>
    </div>
  );
};

export default FAQEditor;
