import React, { useState, useEffect } from 'react';
import { Button, Card, Select, Spin, message } from 'antd';
import ReactQuill from 'react-quill';
import axios from 'axios';
import 'react-quill/dist/quill.snow.css';
import LanguageSelector from '../LanguageSelector';

const FAQEditor = () => {
  const [faqs, setFaqs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedLang, setSelectedLang] = useState('en');
  const [formData, setFormData] = useState({
    question: '',
    answer: '',
    language: 'en'
  });

  const LANGUAGES = [
    { code: 'en', name: 'English' },
    { code: 'hi', name: 'Hindi' },
    { code: 'bn', name: 'Bengali' },
    // Add all 22 languages
  ];

  useEffect(() => {
    const fetchFAQs = async () => {
      try {
        const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/faqs?lang=${selectedLang}`);
        setFaqs(data);
      } catch (error) {
        message.error('Error loading FAQs');
      }
    };
    fetchFAQs();
  }, [selectedLang]);

  const handleSubmit = async () => {
    try {
      setLoading(true);
      const { data } = await axios.post(`${process.env.REACT_APP_API_URL}/faqs`, {
        ...formData,
        autoTranslate: true
      });
      setFaqs([data, ...faqs]);
      message.success('FAQ created successfully!');
      setFormData({ question: '', answer: '', language: 'en' });
    } catch (error) {
      message.error('Error creating FAQ');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <Card 
        title="FAQ Manager" 
        extra={<LanguageSelector value={selectedLang} onChange={setSelectedLang} />}
      >
        <Select
          options={LANGUAGES}
          value={formData.language}
          onChange={lang => setFormData(p => ({ ...p, language: lang }))}
          style={{ width: 200, marginBottom: 16 }}
        />

        <input
          placeholder="Enter question"
          value={formData.question}
          onChange={e => setFormData(p => ({ ...p, question: e.target.value }))}
          className="input-field"
        />

        <ReactQuill
          theme="snow"
          value={formData.answer}
          onChange={value => setFormData(p => ({ ...p, answer: value }))}
          modules={{
            toolbar: [
              ['bold', 'italic', 'underline'],
              ['link', 'blockquote'],
              [{ list: 'ordered' }, { list: 'bullet' }]
            ]
          }}
        />       
        <Button
          type="primary"
          onClick={handleSubmit}
          loading={loading}
          disabled={!formData.question || !formData.answer}
          style={{ marginTop: 16 }}
        >
          Save FAQ
        </Button>

        <div className="faq-list">
          {faqs.map(faq => (
            <Card key={faq._id} style={{ marginTop: 16 }}>
              <h3>{faq.question}</h3>
              <div dangerouslySetInnerHTML={{ __html: faq.answer }} />
              <div className="language-tag">{faq.language.toUpperCase()}</div>
            </Card>
          ))}
        </div>
      </Card>
    </div>
  );
};

export default FAQEditor;
